INSERT INTO `material` VALUES ('6725', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/4718', '蝴蝶花', 'Hu Die Hua', 'Herba Iridis japonicae', 'all-grass of Friged Iris', '蝴蝶花', null, '植物', '蝴蝶花', 'Iris japonica Thunb.', 'Iris japonica Thunb.', null, '为鸢尾科植物蝴蝶花的全草。', '根茎呈不规则结节状。长2－5cm，直径0.7－2.5cm。表面灰褐色，粗糙，可见圆形的茎痕或残留的茎基。须根细长弯曲，下部多已折断，长5－20cm，直径1.5－4mm；表面黄棕色。有明显的纵皱纹及疏生的细根，有时可见纤细的绒毛。质空虚软韧或硬而脆。横断面中央有小木心，木心与外皮间为空隙或黄白色的皮层。臭微弱，味淡微苦。', null, null, null, null, '消肿止痛', null, null, '脾虚便溏者忌服。$$', null, null, null, '春、夏季采收，切段晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
