INSERT INTO `material` VALUES ('6727', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/6811', '蝴蝶藤', 'Hu Die Teng', 'Passiflora papilio Li', null, '蝴蝶藤', null, '植物', '金粟藤', 'Passiflora papilio Li', null, null, '为西番莲科植物金粟藤的全草。', '茎髓呈细圆柱形，长短不一，直径0.4－1cm。银白色或微黄色，表面平坦无纹理。体轻，质松软，可弯曲，以指捏之能使其变形。断面银白色，有光泽，无空心。水浸后，外表及断面均有粘滑感，无气味。', null, null, '味苦、甘，性平。', null, '祛湿', null, null, null, null, null, null, '秋季挖取全株，洗净，切段，晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('6728', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/4928', '蝶兰', 'Die Lan', 'Phalaenopsis wilsonii Rolfe', null, '蝶兰', null, '植物', '蝶兰', null, null, null, '为兰科植物蝶兰的全草。', '全草卷缩成团粒状或缠绕成团。块茎肉质，皱缩成不规则的扁平状，直径5－12mm，类白色或黄白色，多已与茎叶脱落。叶皱缩，灰绿色或黄绿色，膜质柔韧，展平后呈卵圆形或卵状心形，长、宽2.5－7cm（大者可达10cm），先端钝或微尖，基部心形，边缘微波状，基出弧形脉约20条，呈膜翅状突起；叶柄稍扁，长3－7cm（有时长达15cm），直径约2.5mm，灰黄色或黄白色，有细纵纹，基部有时残留管状叶鞘及从两侧伸出的纤细不定根。气微有草菇香，味微甘。|以叶嫩小、色青绿、具草菇香气者为佳。', null, null, null, null, '活血通络', null, null, null, null, null, null, '夏、秋季采收，鲜用或晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
