INSERT INTO `material` VALUES ('7487', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/6253', '铜皮', 'Tong Pi', 'Saurauia napaulensis DC.', null, '铜皮', null, '植物', '尼泊尔水东哥', 'Saurauia napaulensis DC.', null, null, '为猕猴桃科植物尼泊尔水东哥的树皮。', '根粗长，有少数分枝。商品均已切成段，直径3－5cm，长1－4cm，外皮厚0.2－0.5cm。表面紫褐色，较光滑，栓皮易层片状剥落，脱落处显白色粉霜。质坚硬，切面皮部棕褐色，较平坦，木质部黄白色，有细密小孔（导管），略呈同心环排列，中央髓细小，直径约0.2cm，颗粒性。黄白色。', null, null, null, null, '解毒', null, null, null, null, null, null, '全年均可采收，晒干或鲜用。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7488', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/6810', '铜线草', 'Tong Xian Cao', 'Paspalum pasaloides (Michx.) Scribn.', null, '铜线草', null, '植物', '双穗雀稗', null, null, null, '为禾本科植物双穗雀稗的全草。', '全体集结成疏松的团状，上端有分离的残茎，圆柱形，中空，长2.5－6.5cm，外包数层灰白色或黄白色的叶鞘；下端簇生多数须根。须根细长而弯曲，直径1mm，表面黄白色至黄棕色，表皮脱落后显白色，略具纵皱纹。体轻，质软，气微，味淡。', null, null, '味甘，性平。', '肝经。', '活血', null, null, null, null, null, null, '夏季采收，晒干或鲜用。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
