INSERT INTO `material` VALUES ('7490', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/9372', '铜罗伞', 'Tong Luo San', 'Radix Indigoferae incarnatae', 'root of Chinese Indigo', '铜罗伞', null, '植物', '庭藤', 'Indigofera incarnata (Willd.) Nakai', 'Indigofera incarnata (Willd.) Nakai', null, '为豆科植物庭藤的根及全草。', '根圆柱形，头部略膨大，有3－5分枝和须根，多弯曲。长15－45cm，直径0.2－0.8cm，表面灰褐色，有从皱纹和凸起的点状或横长皮孔，有的可见栓皮脱落，脱落处类白色或浅棕褐色。质硬，易折断，断面纤维状，皮部浅棕褐色，木部类白色，有放射状纹理。气微，味微苦。', null, null, '味辛、微酸，性平。', null, '活血止痛', null, null, null, null, null, null, '全年均可采，洗净，晒干或鲜用。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7491', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/9467', '铜罗汉', 'Guo Luo Han', 'Milingtonia hortensis L.', null, '铜罗汉', null, '植物', '老鸦烟筒花', 'Milingtonia hortensis L.', null, null, '为紫葳科植物老鸦烟筒花的树皮或叶。', '全草长30－100cm。茎圆柱形，多分枝，表面淡绿色或黄绿色，略具细棱或纵纹，光滑无毛；质脆，易折断，断面黄白色，髓白色。叶多破碎或脱落，茎上部具总状排列的蒴果，呈羊角状，长4－9.8cm，直径0.4－0.6cm，多开裂，内具中隔。种子扁平，具膜质的翅，气微，味淡。', null, '、|、|祛痰|镇咳|抗炎镇咳、祛痰、抗炎等作用 老鸦烟筒花叶、花等所含黄酮类化合物粗毛豚草素（Ⅰ），有镇咳及明显的祛痰作用；在体外对人体鼻咽癌（KB）细胞有细胞毒性，其ED~5~0为96μg/ml。在研究从花中提取的Ⅰ的抗炎作用时，将Ⅰ与1-^1^4C-花生四烯酸和含有5-脂加氧酶的猪白细胞混悬液共同孵育，结果表明Ⅰ对5-脂加氧酶有显著抑制作用，当Ⅰ的浓度为64μmol/L时，对5-脂加氧酶的抑制率为65％。', '味苦，性凉。', null, '驱虫', null, null, null, null, null, null, '树皮全年均可采，晒干。春、夏季采叶，晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7492', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/5782', '铜脚威灵仙', 'Tong Jiao Wei Ling Xian', 'Clematis terniflora DC.', null, '铜脚威灵仙', null, '植物', '圆锥铁线莲', 'Clematis terniflora DC.', null, null, '为毛莨科植物圆锥铁线莲的根。', '茎藤缠绕或切成段，圆柱形具纵棱，绿褐色，节部稍膨大。叶对生，羽状复叶，小叶5－7片，顶端小叶较大，叶片披针形或狭长圆形，先端渐尖，基部楔形，边缘有不规则的锐齿，质薄脆，常破碎。气微，味微苦辛。', null, null, null, null, '凉血止血', null, null, '剂量不能超过24g。服药后有眼花反应，一般不需处理，1－2h后能自动消失，若有头昏、呕吐反应，可服米泔水解之。', null, null, null, '四季均可采挖，洗净，鲜用或晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7493', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/7888', '铜苋菜', 'Tong Xian Cai', 'Nymphoides indica (L.) O. Kuntze', null, '铜苋菜', null, '植物', '金银莲花', 'Nymphoides indica (L.) O Kuntze', null, null, '为龙胆科植金银莲花的全草。', '全草皱缩，光滑无毛。茎圆柱形，不分枝，形如叶柄，先端单生一叶。叶片近圆形，长3－11cm，基部深心形，全缘；革质。气微，味辛。', null, null, '味甘、微苦，性寒。', null, '养胃阴', null, null, null, null, null, '铜苋菜 Herba Nymphoidis Indicae 产于吉林、江苏、安徽、云南、广东等地。自产自销。', '夏、秋季采收洗净，晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7494', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/6670', '铜身铁骨', 'Tong Shen Tie Gu', 'Maackia tenuifolia (Hemsl.) Hand.-Mazz.', null, '铜身铁骨', null, '植物', '光叶马鞍树', 'Maackia tenuifolia (Hemsl.) Hand.-Mazz.', null, null, '为豆科植物光叶马鞍树的根。', '卷曲呈筒状。外表面绿褐色，易薄片状剥裂，老皮暗灰色。质硬而脆，易折断，断面纤维性。气微香，味淡。', null, null, '味辛，性温。', null, '活血通络', null, null, null, null, null, null, '全年均可采，挖出根后，洗净，鲜用或切片晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7495', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/5769', '铜钱细辛', 'Tong Qian Xi Xin', 'Asarum debile Franch.', null, '铜钱细辛', null, '植物', '铜钱细辛', null, null, null, '为马兜铃科植物铜钱细辛的全草。', '根茎棕黄色，直径1－2mm，节间长0.5－1.7cm，可约至2.5cm；断面类正方形，形成层环棕褐色。根纤细。叶片较小，展平后呈心形，长2.5－4cm，宽3－6cm，先端急尖或钝，基部心形，叶缘有睫毛，上面散生白色柔毛，脉上毛密；叶柄长5－12cm。气芳香，味辛辣。', null, null, '味辛、苦，性温。', '肺、肾经。', '祛风除湿', null, null, null, null, null, '铜钱细辛 Herba Asari Debilis 主产于安徽、湖北、陕西、四川。自产自销。', '5－8月挖取全草，洗净，置通风处阴干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7496', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/8298', '铜钱菌', 'Tong Qian Jun', 'Lactarius insulsus (Fr.) Fr.', null, '铜钱菌', null, '植物', null, null, null, null, null, null, null, null, null, null, '活血通络', null, null, null, null, null, null, null, null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7497', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/3095', '铜锣七', 'Tong Luo Qi', 'Stephania herbacea Gagnep.', null, '铜锣七', null, '植物', '草质千金藤', null, null, null, '为防己科植物草质千金藤的块根。', '块根略呈球形，直径5－20cm。切片或条状块切面灰黄色或淡黄色，可见静脉（三生维管束）环状排列呈同心环状。气微，味苦。', null, null, null, null, '活血止痛', null, null, '孕妇禁服。', null, null, null, '秋季采挖，洗净，切片，晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7498', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/5246', '铜锤玉带草', 'Tong Chui Yu Dai Cao', 'Herba Pratiae begonifoliae', 'all-grass of Common Pratia', '铜锤玉带草', null, '植物', '铜锤玉带草', 'Pratia begonifolia (Wall) Lindll.', 'Pratia begonifolia (Wall) Lindl.', null, '为桔梗科植物铜锤玉带草的全草。', '根圆柱形或纺锤形，下部渐细，有的分枝，长6－20cm，直径1－2cm。表面淡黄白色，微有光泽，皱缩，有扭曲的纵沟，并有横向皮孔斑痕及支根痕，有时可见未刮净的黄棕色或灰棕色栓皮；上端根茎（芦头）长0.5－4cm，直径约1cm，具半朋形的茎痕，呈盘节状。质硬脆，易折断，折断面略不平坦，可见放射状裂隙，皮部类白色，形成层环棕色，木部淡黄色。气微，味微甜、苦。|以根肥大、色白、质充实、味苦者为佳。', null, null, '味辛、苦，性平。', null, '活血', null, null, '孕妇慎服。$$', null, null, null, '夏季采收，洗净，鲜用或晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7499', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/2464', '铜锤草', 'Tong Chui Cao', 'Herba Oxalidis corymbosae', 'all-grass of Corymb Woodsorrel.', '铜锤草', null, '植物', '红花酢浆草', 'O. martiana Zucc.', 'Oxalis corymbosa DC.', null, '为酢浆草科植物红花酢浆草的全草。', '为段片状。茎、枝被疏长毛。叶纸质，皱缩或破碎，棕绿色。花黄色，萼片、花瓣均5枚。蒴果近圆柱形，有5条棱，被柔毛，种子小，扁卵形，褐色。具酸气。味咸而酸涩。', null, null, '味酸，性寒。', '肝、小肠经。', '解毒', null, null, '孕妇禁服。$$', null, null, null, '3－6月采收全草，洗净，鲜用或晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7500', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/1743', '铜锤草根', 'Tong Chui Cao Gen', null, null, '铜锤草根', null, '植物', '铜锤草', null, null, null, '为酢浆草科植物红花酢浆草 Oxalis corymbosa DC.的根。', '为段片状。茎、枝被疏长毛。叶纸质，皱缩或破碎，棕绿色。花黄色，萼片、花瓣均5枚。蒴果近圆柱形，有5条棱，被柔毛，种子小，扁卵形，褐色。具酸气。味咸而酸涩。', null, null, '味酸，性寒。', null, '清热', null, null, null, null, null, null, '秋季挖根，洗净泥土，鲜用或晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7501', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/7915', '铜骨七', 'Tong Gu Qi', 'Rhizoma Anemones davidii', 'rhizome of David Anemone', '铜骨七', null, '植物', '西南银莲花', 'Anemone davidii Franch.', 'Anemone davidii Franch.', null, '为毛莨科植物西南银莲花的根茎。', '根茎长纺锤形，稍弯曲，长1－4cm，直径3－5mm。表面棕黄色至暗棕色，具多数半环突起的节，其上有磷叶痕，斜向交互排列，节上有1－3个突起的根痕。质硬脆，易折断，断面平坦，色白，有粉性，可见淡黄色小点（维管束）6－12个，排列成环。气微，味微酸。|以色棕黄、断面色白者为佳。', null, null, null, null, '活血祛瘀', null, null, '孕妇忌服。', null, null, '铜骨七 Rhizoma Anemones Davidii 产于四川、西藏、云南、贵州、湖南及湖北。自产自销。', '春、夏、秋季采收，晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7502', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/8048', '铧头草', 'Hua Tou Cao', 'Herba Violae patrinii', 'all-grass of Whiteflower violet./all-grass of Longsepal Violet./all-grass of Nepal Haberdleaf Violet.', '铧头草', null, '植物', '戟叶堇菜', 'Viola betonicifolia Smith[V.betonicifolia Smith subsp.nepalensis（Ging.）W.Beck.', 'Viola patrinii DC.', null, '为堇菜科植物戟叶堇菜的全草。', '多皱缩成。主根较粗短。叶纵生，灰绿或枯绿色，具长柄。叶片湿润展开后，叶片箭头状披针形或线装披针形，基部稍下延于叶柄，边缘有浅波状齿。花柄长于叶，花黄白色，可见紫色条纹。蒴果椭圆形。气微，味微苦带粘性。', null, null, '味微苦、辛，性寒。', null, '散瘀消肿', null, null, '孕妇慎服。', null, null, '铧头草 Herba Violae Betonicifoliae 产于长江流域以南各地。多自产自销。', '夏、秋季采收全草，洗净，除去在职，鲜用或晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7503', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/7072', '铧尖草', 'Hua Jian Cao', 'Viola inconspicus Bl.', null, '铧尖草', null, '植物', '长萼堇菜', 'Viola betonicifolia Smith ssp.nepalensis W.Beck.', null, null, '为堇菜科植物长萼堇菜的全草。', '叶片三角状卵形或舌状三角形，基部宽心形，稍下延于叶柄，有两垂片，有的两面皆可见少数短毛。花距短囊形，长约2.5cm。', null, null, '味苦、辛，性寒。', null, '活血祛瘀', null, null, null, null, null, '铧尖草 Herba Violae Inconspicui 产于长江流域以南各地。多自产自销。', '夏、秋季采集全草，洗净，除去杂质，鲜用或晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7504', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/8054', '铰剪藤', 'Jiao Jian Teng', 'Holostemma annulare (Roxb.) K. Schum.', null, '铰剪藤', null, '植物', '铰剪藤', 'Asclepias Roxb.', null, null, '为萝蘑科植物铰剪藤的全株。', '根圆柱形，直径1－3cm，常切成2－5mm厚的斜片；外表面灰棕色，较粗糙，具裂纹及皮孔；切段面黄色，木部有细密小孔，形成层环波状弯曲，髓部疏松，淡棕色。茎类圆柱形，灰褐色，具皮孔，被微毛。也对生，多皱缩，完整者展平后呈倒卵形或卵状长长圆形，长3－8cm，宽1.5－4cm，仅叶脉被微毛；嫩、枯叶均具乳汁。叶柄长3－10mm，被短毛，气微，味苦。以枝嫩、叶多、根粗壮、切面黄色、无杂质者为佳。', null, null, '味甘，性平。', null, '补血', null, null, null, null, null, null, '冬季采收，洗净，切段，晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7505', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/6424', '银不换', 'Yin Bu Huan', 'Radix Cycleae barbatae', 'root of Barbate Cyclea', '银不换', null, '植物', '毛叶轮环藤', 'Cyclea barbata Miers', 'Cyclea barbata Miers', null, '为防己科植物毛叶轮环藤的根。', '根圆柱形或扭曲，稍呈连珠状凸起，长10－20cm，直径1－2.5cm。表面黑褐色，有弯曲的纵沟和少数支根痕。质硬，断面黄白色，有放射状纹理和小孔。气微，味微苦。|以条匀，坚实者为佳。', null, null, null, null, '活血止痛', null, null, null, null, null, null, '四季均可采挖，洗净泥土，去粗皮，切段，晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7506', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/6120', '银丝杜仲', 'Yin Si Du Zhong', 'Euonymus vagans Wall. et Roxb.', null, '银丝杜仲', null, '植物', '藤刺卫矛', 'Euonymus vagans Wall.et Roxb.', null, null, '为卫矛科植物藤刺卫矛的茎皮。', '本品外表面灰色，平坦或粗糙，有明显的横皱纹，质脆，易折断，折断后有断弹性白丝。气微，位淡。', null, null, null, null, '祛风除湿', null, null, null, null, null, '银丝杜仲 Cortex Euonymi Vagantis 产于广西、四川、贵州、云南、西藏等地。自产自销。', '夏、秋季采收，剥皮鲜用或晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7507', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/9773', '银兰', 'Yin Lan', 'Cephalanthera erecta (Thunb.) Bl.', null, '银兰', null, '植物', '银兰', null, null, null, '为兰科植物银兰的全草。', '根茎纤细，直径1－1.5mm，每隔1.4－2.5cm，有一假鳞茎。假鳞茎卵状长圆形、类圆锥形，长0.8－1.5cm，直径4－8mm，表面具细纵棱纹，近根茎一侧具1凹槽，基部不收缩成柄状。气微，味淡。', null, null, null, null, '清热利湿', null, null, null, null, null, null, '全年均可采收，洗净，鲜用。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7508', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/8138', '银南星', 'Yin nan Xing', 'Arisaema bathycoleum Hand.-Mazz.', null, '银南星', null, '植物', '银南星', null, null, null, '为天南星科植物银南星的块茎。', '块茎扁圆形，直径1.5－6.5cm，表面棕色，周围麻点状根痕细小，周边有较多突出的侧芽。气微，微麻舌。', null, null, null, null, '和胃止呕', null, null, null, null, null, null, '秋季采挖，除去须根及外皮，晒干或烘干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7509', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/5788', '银叶柳', 'Yin Ye Liu', 'Cortex Salicis chienii', 'bark of Silverleaf willow', '银叶柳', null, '植物', '银叶柳', 'Salix chienii Cheng', 'Salix chienii Cheng', null, '为杨柳科植物银叶柳的根或枝叶。', '种子细小，倒披针形，长1－2mm，黄褐色或淡灰黑色。表面有纵沟，顶端簇生白色丝状绒毛，长2－4mm，成团保卫在种子外部。', null, null, '味辛、苦，性寒。', null, '祛风止痒', null, null, null, null, null, null, '根多在夏、1秋季采收，枝叶宜在春、夏季采收，鲜用或晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7510', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/1744', '银叶树', 'Yin Ye Shu', 'Heritiera littoralis Dryand.', null, '银叶树', null, '植物', '银叶树', 'Heritiera littoralis Dryand.', null, null, '为梧桐科植物银叶树的种子。', '茎类圆形，有纵皱，幼枝被短柔毛。叶卵圆形或近圆形，长6－15cm，宽4.5－8.5cm。先端长渐尖，中部以上常有浅裂，基部心形，边缘有不规则的锯齿，上面黄绿色，被稀疏的星状短柔毛，下面色较浅，被白色星状茸毛，基生脉5－7条，叶柄长3－10mm，被毛。', null, null, '味甘、涩，性平。', null, '涩肠止泻', null, null, null, null, null, null, '夏、秋季采收，鲜用或晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7511', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/7783', '银合欢', 'Yin He Huan', 'Cortex Leucaenae glaucae', 'bark of Hedge Acacia/bark of Whitepopinac Leadtree', '银合欢', null, '植物', '银合欢', 'Leucaena glauca (L) Benth', 'Leucaena glauca (L) Benth', null, '为豆科植物银合欢的根皮。', '根呈长圆柱形，具分枝，长10－30cm，表面淡棕褐色，具细纵皱纹，皮孔呈点状或横向延长疤状。茎圆柱形，较细，长约50cm，多分枝或丛生，表面灰黄色至灰褐色，木质。叶为三出复叶，小叶片狭卵形、倒卵形或椭圆形，长1－2.5cm，宽0.5－1.5cm，先端圆钝，稍具短尖，全缘，绿色或绿褐色，上面近无毛或被平伏短毛，背面毛较密集。有时可见腋生的总状花序，总花梗长4－15cm，花梗无关节，花萼杯状，长约4.5mm，被疏毛，花冠蝶形。荚果倒卵形。气微、味淡，具豆腥气。', null, null, '味甘，性平。', null, '消肿毒', null, null, null, null, null, null, '秋、冬季采收根皮，洗净，切碎晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7512', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/8626', '银扁担', 'Yin Bian Dan', 'Radix Aqiulegiae incurvatae', 'root of Chinling Mountains Columbine', '银扁担', null, '植物', '秦岭耧斗菜', 'Aqiulegin incurvats Hsiao', 'Aqiulegin incurvats Hsiao', null, '为毛莨科植物秦岭耧斗菜的根。', '根常扭曲，长10－15cm，直径0.8－1.5cm，少分枝；表面棕色或棕褐色，具不规则的纵皱纹及少数侧根痕。根头部有茎基、叶基及棕黄色须状叶基维管束。质坚实，木质性，断面不平整，黄棕色。气微，味苦。', null, null, null, null, '活血祛瘀', null, null, null, null, null, null, '夏季采挖，去须根，洗净，阴干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('7513', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/1752', '银木荷皮', 'Yin Mu He Pi', 'Schima argentea Pritz.', null, '银木荷皮', null, '植物', '银木荷', 'Schima argentea Pritz', null, null, '为山茶科植物银木荷的茎皮或根皮。', '果实呈不规则的球形，直径约2mm。表面紫红色或暗红色，皱缩，先端有残存花柱，有的基部可见花萼和果柄。气香，味微苦。', null, null, '味苦，性平，有毒。', '大肠经。', '驱虫', null, null, null, null, null, null, '秋季采集，洗净，切段，晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
