INSERT INTO `material` VALUES ('7637', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/8668', '阴香叶', 'Yin Xiang Ye', 'Folium Cinnamomi burmannii', 'Leaf of Burmann Cinnamon', '阴香叶', null, '植物', '阴香皮', 'Cinnamomum subavenium Miq.', 'Cinnamomum burmannii (Nees) Bl.', null, '为樟科植物阴香 Cinnamomum burmannii（C.G.et Th.Nees）Bl.的叶。', '茎皮呈槽状或片状，厚约 3mm。外表面棕灰色，粗糙，有圆形突起的皮孔和灰白色地衣斑块，有时外皮部分刮去而现凹下的皮孔痕；内表面棕色，平滑。质坚，断面内层呈裂片状。气香，味微甘、涩。', null, null, null, null, '祛风', null, null, null, null, null, null, '秋季采收叶，晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
