INSERT INTO `material` VALUES ('8317', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/6149', '鹅绒藤', 'E Rong Teng', 'Sucus Cynanchi chinensis', 'juice of Chinese Swallowwort', '鹅绒藤', null, '植物', '鹅绒藤', 'Cynanchum chinense R.Br.', 'Cynanchum chinense R.Br.', null, '为萝蘑科植物鹅绒颈中的白色浆乳汁及根。', '根呈圆柱形，长约20cm，直径5－8mm。表面灰黄色，平滑或有细皱纹，栓皮易剥离，剥离处显灰白色。质脆，易折断，断面不平坦，黄色，中空。气微，味淡。', null, null, '味苦，性寒。', null, '消食积', null, null, null, null, null, '鹅绒藤根Radix Cynanchi Chinensis 产于辽宁、河北、内蒙古等地。自产自销。', '夏、秋季间随采乳汁随用；根挖出后洗净，晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('8318', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/10086', '鹅肉', 'E Rou', 'Carnis Anseris domestica', 'Goose as food', '鹅肉', null, '动物', null, null, 'Anser domestica Geese', null, '为鸭科动物家鹅的肉。原动物：家鹅/L：Anser cygnoides domestica Brisson/鹅/舒雁/[可鸟]/家雁。', '鸭蛋呈卵圆形，长径5－9cm，表面类白色或淡青绿色，外壳坚硬，光滑，皮破后内有白色厚膜，较坚韧。蛋清呈胶体，无色半透明，遇热固化变性成白色固体，蛋清内有2条系膜与蛋黄相连。蛋黄黄色或橘红色，胶体外有核膜包围，遇热易固化呈固体，手搓易呈粉状。气微腥，味淡。', null, null, null, null, '和胃', null, null, '湿热内蕴者禁食。$$', null, null, null, '四季均可宰杀，冬季最好，除去羽毛及内脏，取肉鲜用。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
INSERT INTO `material` VALUES ('8319', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/6228', '鹅肠草', 'E Yang Cao', 'Myosoton aquaticum Moench.', null, '鹅肠草', null, '植物', '牛繁缕', 'Malachium aquaticum （L.）Fries', null, null, '为石竹科植物牛繁缕的全草。', '全草长20－60cm。茎光滑，多分枝；表面略带紫红色，节部和嫩枝梢处更明显。叶对生，膜质；完整叶片宽卵形或卵状椭圆形，长1.5－5.5cm，宽1－3cm，先端锐尖，下部叶叶柄长5－18mm，疏生柔毛。花白色，生于枝端或叶腋。蒴果卵圆形。种子近圆形，褐色，密布显著的刺状突起。气微，味淡。', null, null, '味甘、酸，性平。', '肝、胃经。', '散瘀消肿', null, null, null, null, null, null, '春季生长旺盛时采收，鲜用或晒干。', null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
