INSERT INTO `material` VALUES ('8336', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/6633', '鹤肉', 'He Rou', 'Carnis Grus japonensis', 'Red-crested crane/Red-crowned crane', '鹤肉', null, '动物', null, null, 'Grus japonensis (P.L.S.Muller)', null, '为鹤科动物丹顶鹤的肉。原动物：丹顶鹤/L：Grus japonensi （P. L. M. Muller）/鹤/白鹤/仙鹤/仙禽/胎禽/红顶子（通称）。', '鲜肝红色或赭红色，1－4叶连在一起，大叶长4－6cm或更长，质软嫩，有血液。干品2棕褐色或紫褐固体，较硬，有焦腥气。', null, null, null, null, '补气', null, null, '（鹤）顶血大毒，服之即死。$$鹤肉有毒，顶血饮之立死。$$', null, null, null, null, null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
