INSERT INTO `material` VALUES ('8544', 'http://oldtcm.med.wanfangdata.com.cn/Home/Detail/1615', '黄羊喉', 'Huang Yang Hou', null, null, '黄羊喉', null, '动物', null, null, null, null, '为牛科动物黄羊 Procapra gutturosa Pallas的咽喉。原动物：参见“黄羊肉”条。', '本品呈长圆锥形而侧扁，略呈“S”形，长约20cm。表面灰棕色或灰黑色，不甚光滑，除先端外，角中下部均有等距的椭圆形环脊17－20个，其下部间距较密，约5mm，环脊的一侧较平坦，不连成环状。内有骨塞，透明。气味弱。', null, null, null, null, '消肿毒', null, null, null, null, null, null, null, null, null, null, null, '2018-05-20 17:27:28', null, '2018-05-20 17:27:28');
